package javacrashcourse;

public class JavaIfElse {
	
	boolean letshavecoffee = true;                             //boolean take default value as false

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		JavaIfElse coffee = new JavaIfElse();
		
         //objecttname.		
		if(coffee.letshavecoffee) {
			System.out.println("fill the cup");
		}
		else {
			System.out.println("drink the coffee");
		}

	}

}
