package javacrashcourse;


public class JavaLooping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
      //for(initialization;condition;increament)                 //for loop
		/*
		 * for(int times=0;times<25;times++) {
		 * System.out.println("I can program,I can learn"); }
		 */
		
		
	   
		/* 
		 * int times=0;                                          //while loop
		 * while(times<25){                                     
		 * System.out.println("I can program,I can learn"); times++; }
		 */
		
		
		
	   
	   int times=0;                                                //do-while
	   do {
		   System.out.println("I can program,I can learn");
			  times++;
	} while (times<25);
	}
}
